import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import NewProduct from "./NewProduct";
import {BASE_PRODUCT_URL, get} from "../requests/requests";
import {Link} from "@material-ui/core";
import Product from "./Product";

const useStyles = makeStyles((theme) => ({
    seeMore: {
        marginTop: theme.spacing(3),
    },
    fab: {
        position: 'absolute',
        bottom: theme.spacing(2),
        right: theme.spacing(2),
    },
}));

const Products = () => {
    const classes = useStyles();
    const [openNew, setOpenNew] = useState(false);
    const [open, setOpen] = useState(false);
    const [produtos, setProdutos] = useState([]);
    const [produto, setProduto] = useState({});

    const handleCloseNew = () => {
        setOpenNew(false);
    }
    const handleOpenNew = () => {
        setOpenNew(true);
    }

    const handleClose = () => {
        setOpenNew(false);
    }
    const handleOpen = () => {
        setOpenNew(true);
    }

    const getProduto = (event, id) => {
        event.preventDefault();
        const response = get(BASE_PRODUCT_URL + id, null);
        response.then(res => {
            console.log(res.data);
            setProduto(res.data);
        }, err => {
            console.log(err);
        });
    }

    const getProdutos = () => {
        const response = get(BASE_PRODUCT_URL, null);
        response.then(res => {
            console.log(res.data.content);
            setProdutos(res.data.content);
        }, err => {
            console.log(err);
        });
    }

    useEffect(() => {
        getProdutos();
    }, []);

    return (
        <React.Fragment>
            <Table size="small">
                <TableHead>
                    <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Nome</TableCell>
                        <TableCell>Pre√ßo</TableCell>
                        <TableCell align="right">Quantidade</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {produtos.map((p) => (
                        <TableRow key={p.id}>
                            <TableCell>{p.id}</TableCell>
                            <TableCell><Link onClick={e => getProduto(e, p.id)}>{p.name}</Link></TableCell>
                            <TableCell>{p.price}</TableCell>
                            <TableCell align="right">{p.quantity}</TableCell>
                        </TableRow>
                    ))}
                    {/*{rows.map((row) => (*/}
                    {/*    <TableRow key={row.id}>*/}
                    {/*        <TableCell>{row.id}</TableCell>*/}
                    {/*        <TableCell>{row.name}</TableCell>*/}
                    {/*        <TableCell>{row.amount}</TableCell>*/}
                    {/*        <TableCell align="right">100</TableCell>*/}
                    {/*    </TableRow>*/}
                    {/*))}*/}
                </TableBody>
            </Table>
            <div className={classes.seeMore} />
            <Fab className={classes.fab} color="primary" onClick={handleOpenNew}>
                <AddIcon />
            </Fab>
            <NewProduct open={openNew} handleClose={handleCloseNew} getProducts={() => getProdutos()} />
            <Product open={open} handleClose={handleClose} getProducts={() => getProdutos()} product={produto} />
        </React.Fragment>
    );
}

export default Products;
