import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import TextField from "@material-ui/core/TextField";
import {Autocomplete} from "@material-ui/lab";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TableBody from "@material-ui/core/TableBody";
import DeleteIcon from '@material-ui/icons/Delete';

const useStyles = makeStyles((theme) => ({
    appBar: {
        position: 'relative',
    },
    title: {
        marginLeft: theme.spacing(2),
        flex: 1,
    },
    form: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
        },
    },
}));

const clients = [
    {id: 1, name: 'rafael'},
    {id: 2, name: 'vitoria'},
]

const productList = [
    {id: 1, name: 'bola', price: 10.05, quantity: 100},
    {id: 2, name: 'grip', price: 10.05, quantity: 100},
    {id: 3, name: 'camiseta', price: 10.05, quantity: 100},
    {id: 4, name: 'tenis', price: 10.05, quantity: 100},
    {id: 5, name: 'raquete', price: 10.05, quantity: 100},
]

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

const NewOrder = ({open, handleClose}) => {
    const classes = useStyles();
    const [client, setClient] = useState(null);
    const [product, setProduct] = useState(null);
    const [products, setProducts] = useState([]);

    return (
        <Dialog fullScreen open={open} onClose={handleClose} TransitionComponent={Transition}>
            <AppBar className={classes.appBar}>
                <Toolbar>
                    <IconButton edge="start" color="inherit" onClick={handleClose} aria-label="close">
                        <CloseIcon />
                    </IconButton>
                    <Typography variant="h6" className={classes.title}>
                        Pedido
                    </Typography>
                    <Button autoFocus color="inherit" onClick={handleClose}>
                        Salvar
                    </Button>
                </Toolbar>
            </AppBar>
            <div style={{width: '80%', alignContent: 'center', marginLeft: 'auto', marginRight: 'auto', marginTop: 10}}>
                <form className={classes.form}>
                    <Autocomplete
                        id="clients-combo-box"
                        value={client}
                        onChange={(event, newValue) => {
                            console.log(newValue);
                            setClient(newValue);
                        }}
                        options={clients}
                        getOptionLabel={(product) => product.name}
                        renderInput={(params) => <TextField {...params} label="Cliente" fullWidth/>}
                    />
                    <Autocomplete
                        id="products-combo-box"
                        value={product}
                        onChange={(event, newValue) => {
                            const np = {
                                id: newValue.id,
                                name: newValue.name,
                                quantity: 0,
                                max: newValue.quantity,
                            }
                            products[np.id] = np;
                            setProducts([...products]);
                            setProduct(null);
                        }}
                        options={productList}
                        getOptionLabel={(product) => product.name}
                        renderInput={(params) => <TextField {...params} label="Adicionar produto" fullWidth/>}
                    />
                    <Table size="small">
                        <TableHead>
                            <TableRow>
                                <TableCell>ID</TableCell>
                                <TableCell>Nome</TableCell>
                                <TableCell>Quantidade</TableCell>
                                <TableCell align="right" />
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {products.filter((p) =>!!p).map((row) => (
                                <TableRow key={row.id}>
                                    <TableCell>{row.id}</TableCell>
                                    <TableCell>{row.name}</TableCell>
                                    <TableCell>{row.quantity}</TableCell>
                                    <TableCell align="right">
                                        <IconButton onClick={() => {
                                            products[row.id] = null;
                                            setProducts([...products]);
                                        }}>
                                            <DeleteIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                    {/*<TextField*/}
                    {/*    autoFocus*/}
                    {/*    id="name"*/}
                    {/*    label="Nome"*/}
                    {/*    type="text"*/}
                    {/*    fullWidth*/}
                    {/*/>*/}
                </form>
            </div>
        </Dialog>
    );
}

export default NewOrder;
